##function Make sure the internet is connected
function ConX () {
command -v wget &> /dev/null
if [ $? -ne 0 ];then
	pkg install wget -y &> /dev/null
	if [ $? -ne 0 ];then
		pkg update
		pkg upgrade -y
		pkg install wget -y
	fi
fi
