if [ $? -eq "0" ]; then
	printf "\n[-] internet status: [connected ! :) ]\n\n"
else
	printf "\n[-] internet status: [ ${BRed}not connected$yellow ! ${BRed}:( ${white}]\n[${BRed}!${white}] error: your not connected to internet ${BRed}!!!${ncolor} \n\nMake sure your internet connection is working${white}\n**************** [ ${BRed}Exiting ${white}] ****************\n"
	exit 1
fi
}

