#Check if the password
Passwd () {
tm=0
$logenamE
while :
	do
	echo -n "[!] password for $logenamE-linux: " 
	read -r pas
	if [ ! -x ${folder}.pas ];then
		pas=$(tr '[:upper:]' '[:lower:]' <<<"$pas")
		pas=$(grep -o '[[:lower:]]' <<<"$pas"| awk NF=NF RS= OFS=)
		if [[ $pas == $(grep -o '[[:lower:]]' <<< cat ${folder}.pas| awk NF=NF RS= OFS=) ]];then 
			echo $pas > ${folder}.pas
		fi
	fi
	if [[ $pas == `cat ${folder}.pas` ]];then 
		white=${blue}
		$logenamE
		white='\e[1;37m'
		dist=$(tr '[:upper:]' '[:lower:]' <<<"$logenamE")
		printf "${white}Welcome to ${blue}${logenamE}-Linux${white} script v1.$(($(cat ${folder}.v)-2))\n\n"
		echo "DIST+=($dist)" >> ${folder}functions.sh            
		#### line to mount /sdcard directly             
		# -b /sdcard      -b /storage/      
		unset LD_PRELOAD && proot --link2symlink -0 -r ${folder}$dist -b /dev -b /proc -b ~:/home -w /home /usr/bin/env -i HOME=/root PATH=/bin:/usr/bin:/sbin:/usr/sbin:/usr/local/bin:/usr/local/sbin:/usr/games:/usr/local/games TERM=xterm-256color LANG=C.UTF-8 /bin/bash --login
		break
	else
		echo -e "Sorry, [ ${BRed}${pas}$white ] try again."
	fi
	((tm++))
	if [[ $tm -eq 3 ]];then
		echo "! : 3 incorrect password attempts"
		sleep 2
		bash
		break
	fi
done
}

