## Changing password
Chang_psswrd () {
$logenamE
printf "\n${blue}Changing password for labs ${BRed}$(cat ${folder}.pas)${white}\n\n"
pps=0
while true ;do
	read -p "Current password : " paswd
	if [ "$paswd" == $(cat ${folder}.pas) ];then
		pps1=0
		while true ;do
			read -p "New password: " new
			read -p "Retype new password: " new1
			if [ "$new" == $new1 ];then
				echo "DIST+=($NaMe)" >> ${folder}functions.sh            
				echo "$new" > ${folder}.pas
				chmod +x ${folder}.pas
				echo -e "${BBlack}${new}${white}: password updated successfully\n"
				sleep 2;bash
				break
			else
				echo "Sorry, passwords do not match. [ $new != $new1 ]"
			fi
			if [[ $pps1 -eq 2 ]];then
				echo "! : 3 incorrect password attempts"
				echo
				echo "passwd: Authentication token manipulation error"
				echo "passwd: password unchanged";sleep 2
				bash
				break
			fi
			((pps1++))
		done
		break
	else
		echo -e "Sorry, [ ${BRed}${paswd}$white ] try again."
	fi
	if [[ $pps -eq 2 ]] ;then
		echo "! : 3 incorrect password attempts"
		echo
		echo "passwd: Authentication token manipulation error"
		echo "passwd: password unchanged";sleep 2
		bash
		break
	fi
	((pps++))
done
}
